import {AfterViewInit, Component, OnInit, ViewEncapsulation} from '@angular/core';
import {InspiniaHelper} from './inspinia-helper';

@Component({
  selector: 'inspinia-template',
  templateUrl: './inspinia-template.component.html',
  styleUrls: ['./inspinia-template.component.scss'],
  encapsulation: ViewEncapsulation.None
})
export class InspiniaTemplateComponent implements OnInit, AfterViewInit {

  constructor(private helper: InspiniaHelper) { }

  ngOnInit() {
  }

  ngAfterViewInit() {
    this.helper.adjustSize();
  }

  toggleNav() {
    this.helper.toggleNavMenu();
  }
}
